std::ostream& operator<<(std::ostream& os, StopFindAction value) {
  switch(value) {
    case StopFindAction::kStopFindActionClearSelection:
      return os << "StopFindAction::kStopFindActionClearSelection";
    case StopFindAction::kStopFindActionKeepSelection:
      return os << "StopFindAction::kStopFindActionKeepSelection";
    case StopFindAction::kStopFindActionActivateSelection:
      return os << "StopFindAction::kStopFindActionActivateSelection";
    default:
      return os << "Unknown StopFindAction value: " << static_cast<int32_t>(value);
  }
}

std::ostream& operator<<(std::ostream& os, FindMatchUpdateType value) {
  switch(value) {
    case FindMatchUpdateType::kFinalUpdate:
      return os << "FindMatchUpdateType::kFinalUpdate";
    case FindMatchUpdateType::kMoreUpdatesComing:
      return os << "FindMatchUpdateType::kMoreUpdatesComing";
    default:
      return os << "Unknown FindMatchUpdateType value: " << static_cast<int32_t>(value);
  }
}

